namespace RK\DownLoadModule\Entity\Factory\Base;

use RK\DownLoadModule\Entity\FileEntity;

/**
 * Entity initialiser class used to dynamically apply default values to newly created entities.
 */
abstract class AbstractEntityInitialiser
{
    /**
     * Initialises a given file instance.
     *
     * @param FileEntity $entity The newly created entity instance
     *
     * @return FileEntity The updated entity instance
     */
    public function initFile(FileEntity $entity)
    {
        $entity->setEndDate(new \DateTime('2099-12-31'));

        return $entity;
    }

}
